import { useState } from 'react'
import { NAV_BAR_ITEMS } from '../../data/navbaritems.data'
import { useLocation, useNavigate } from 'react-router-dom'
//import { Dropdown } from './Nav Components/Dropdown.component'

import asucaaLogo from "../../assets/navigation/asucaaLogo.jpg"
import React from 'react'

const NavBar = () => {
    const [scrolled, setScrolled] = useState(false)

    const location = useLocation().pathname

    const navigate = useNavigate()

    window.addEventListener('scroll', () => {
        if (window.scrollY >= 48) {
            setScrolled(true)
        } else if (scrolled) {
            setScrolled(false)
        }
    })

    return (
        <nav
            className={`h-20 top-0 ${
                scrolled
                    ? 'bg-white shadow-xl shadow-slate-700/10' 
                    : 'bg-white'
            } sticky transition-all z-50 w-screen flex gap-3 select-none ease-in-out duration-300 px-10 py-2 items-center justify-around`}
        >
            <div
                id='logo-container'
                className='cursor-pointer flex items-center ml-6 h-18 w-18'
                onClick={() => {
                    navigate('/')
                }}
            >
                <img src={asucaaLogo} className='pointer-events-none' />
            </div>
            <div className='flex justify-center items-center h-full w-full'>
                <ul
                    id='navbar-items-container'
                    className='flex justify-between gap-7 text-lg'
                >
                    {NAV_BAR_ITEMS.map((item) => {
                        const { path, label, subItems} = item
                        const selected = path === location
                        return (
                            <li
                                key={label}
                                onClick={() => {
                                    if (!subItems) {
                                        navigate(path)
                                    }
                                }}
                                className={`text-dark-red cursor-pointer ${
                                    selected
                                        ? 'opacity-100'
                                        : 'opacity-80 hover:opacity-100'
                                } transition-opacity duration-300 ${
                                    label === 'API' && 'mb:hidden'
                                }`}
                            >
                                {
                                    subItems
                                    ? <Dropdown items={subItems}>{label}</Dropdown>
                                    : label
                                }
                                {item.new && (
                                    <span className='inline-block ml-1 p-1 bg-accent rounded-md text-white uppercase text-base'>
                                        New
                                    </span>
                                )}
                            </li>
                        )
                    })}
                </ul>
            </div>
            <ul className='mr-16 flex flex-row justify-center items-center'>
                <li>
                <a href='/'>
                        <div className='m-1 h-10 w-28 text-lg text-white leading-more-loose bg-gold rounded-full'>
                            Donate
                        </div>
                    </a>
                </li>
                <li>
                    <a href='/newsletter'>
                        <div className='m-1 h-12 w-36 text-lg leading-extra-loose border-black border-2 border-solid rounded-full'>
                            Newsletter
                        </div>
                    </a>
                </li>
            </ul>
        </nav>
    )
}

export default NavBar
